import react from 'react';
import '../styles/app.scss';
import { getPosts, setPosts } from '../store/actions/posts';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import axios from 'axios';
import { API_URL } from '../constants';
import fetch from 'isomorphic-fetch'



class Homepage extends React.Component {
  static async getInitialProps ({ store }) {
    const postsFetch = await fetch(`${API_URL}/posts`);
    const posts = await postsFetch.json()
    store.getState();
    store.dispatch(setPosts(posts));
    return {posts}
  }
  componentDidMount () {

  }

  render () {
    return (
      <div>
        {console.log(this.props.posts)}
        {this.props.posts ? this.props.posts.map((post)=>{
          return(
            <div style={{marginBottom:'2.5rem'}}>
              <p style={{fontWeight:'700'}}>{post.title}</p>
              <p>{post.body}</p>
            </div>
          )
        }): null}
      </div>
    );
  }
}
const mapStateToProps = state => {
  console.log("state",state);
  return {
    posts: state.posts
  }};

const mapDispatchToProps = dispatch => ({
  setPosts: bindActionCreators(setPosts, dispatch)
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Homepage);
