import axios from 'axios';
import { API_URL, GET_COMMENTS } from '../../../constants';

export const getComments = () => async dispatch => {
  const posts = axios.get(  `${API_URL}/comments`).then(response => response.data.json()).then(data=>data).catch(e=>{
    console.error(e);
    return {error: e}
  });

  dispatch({
    type: GET_COMMENTS,
    payload: posts
  });
};
