import react from 'react';
import '../styles/app.scss';
import { getPosts, setPosts } from '../store/actions/posts';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import axios from 'axios';
import { API_URL } from '../constants';
import fetch from 'isomorphic-fetch';
import { Link, Router } from '../routes';


class Homepage extends React.Component {
  static async getInitialProps ({ store }) {
    store.dispatch(getPosts()).then((e) => {
      console.log("random şey",e)
      console.log('store state @ getInitialProps', store.getState());
      return {};
    });
  }

  render () {
    return (
      <div>
        <button onClick={() => {
          Router.pushRoute('comments', { slug: 'hahaha' });
        }}>Comments
        </button>
        {this.props.posts.posts ? this.props.posts.posts.map((post) => {
          return (
            <div style={{ marginBottom: '2.5rem' }} key={post.id}>
              <p style={{ fontWeight: '700' }}>{post.title}</p>
              <p>{post.body}</p>
            </div>
          );
        }) : null}
      </div>
    );
  }
}

export default connect(state => state)(Homepage);

