import axios from 'axios';
import { API_URL, GET_POSTS } from '../../../constants';


export const getPosts = () => ({
  type: GET_POSTS,
  payload: Promise.resolve(
    axios.get(`${API_URL}/posts`)
      .then(async res => {
        return res.data;
      }))
    .catch(e => {
      console.log('ERROR:GET_POSTS ', e);
    })
});
