import axios from 'axios'
import {API_URL, GET_POSTS} from '../../constants';

export const getPosts = () => async dispatch => {
  const posts = axios.get(  `${API_URL}/posts`).then(response => {
    return response.data
  }).catch(e=>{
    console.error(e);
    return {error: e}
  });

  dispatch({
    type: GET_POSTS,
    payload: posts
  });
};
