import axios from 'axios';
import { API_URL, GET_POSTS, SET_POSTS } from '../../../constants'
import fetch from 'isomorphic-fetch'



export const getPosts = async () => ({
  type: GET_POSTS,
  payload: await Promise.resolve(
    axios.get(`${API_URL}/posts`)
      .then(async res => {
        return res.data;
      }))
    .catch(e => {
      console.log('ERROR:GET_POSTS ', e);
    })
});



export const setPosts = (posts) => dispatch => {
  console.log("inside set posts");
  dispatch({
    type: SET_POSTS,
    payload: posts
  })
}
