import react from 'react';
import '../styles/app.scss';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {getComments} from '../store/actions/comments';
import { Router } from '../routes';
import { getPosts } from '../store/actions/posts';


class Comments extends React.Component {
  static async getInitialProps ({ store, query }) {
    await store.dispatch(getComments()).then(async (comments) => {
      return {comments: await comments, postId:query.slug};
    });
  }
  componentDidMount () {
    console.log(this.props)
  }

  render () {
    return (
      <div>
        <button onClick={()=>{Router.pushRoute('/')}}>Posts</button>
        {this.props.comments.comments ? this.props.comments.comments.map((comment) => {
          if (comment.postId)
          return (
            <div style={{ marginBottom: '2.5rem' }} key={comment.id}>
              <p style={{ fontWeight: '700' }}>{comment.name} - {comment.email}</p>
              <p>{comment.body}</p>
            </div>
          );
        }) : null}
      </div>
    );
  }
}
const mapStateToProps = state => ({
  comments: state.comments,
  postId: state.postId
});

const mapDispatchToProps = dispatch => ({
  getComments: bindActionCreators(getComments, dispatch),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Comments);
