import axios from 'axios';
import fetch from 'isomorphic-fetch'
import { API_URL, GET_COMMENTS } from '../../../constants';
/*
export const getComments = () => async dispatch => {
  const posts = axios.get(  `${API_URL}/comments`).then(async (response) => {
    console.log(response.data);
    return await response.data.json();
  }).then(data=>data).catch(e=>{
    console.error(e);
    return {error: e}
  });

  dispatch({
    type: GET_COMMENTS,
    payload: posts
  });
};
 */
export const getComments = () => async dispatch => {
  const postsFetch = fetch(`${API_URL}/comments`)
  const posts = await postsFetch.json();
  dispatch({
    type: GET_COMMENTS,
    payload: posts
  })
  return posts;
}
