import axios from 'axios';
import { API_URL, GET_POSTS, SET_POSTS } from '../../../constants';
import fetch from 'isomorphic-fetch'



export const getComments = () => async dispatch => {
  const posts = axios.get(  `${API_URL}/posts`).then(response => {
    return response.data
  }).catch(e=>{
    console.error(e);
    return {error: e}
  });

  dispatch({
    type: GET_POSTS,
    payload: posts
  });
};



export const setPosts = (posts) => dispatch => {
  console.log("inside set posts");
  dispatch({
    type: SET_POSTS,
    payload: posts
  })
}
